import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-password-strength',
  templateUrl: './password-strength.component.html',
  styleUrls: ['./password-strength.component.css']
})
export class PasswordStrengthComponent {
  passwordForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.passwordForm = this.fb.group({
      password: ['', [Validators.required]]
    });

    this.passwordForm.get('password')?.valueChanges.subscribe(value => {
      this.updatePasswordStrength();
    });
  }

  passwordStrength: string = '';

  updatePasswordStrength() {
    const password = this.passwordForm.get('password')?.value;
    const hasLetters = /[a-zA-Z]/.test(password);
    const hasDigits = /\d/.test(password);
    const hasSymbols = /[!@#\$%\^\&*\)\(+=._-]/.test(password);

    if (password.length < 8) {
      this.passwordStrength = 'weak';
    } else if ((hasLetters && hasSymbols) || (hasLetters && hasDigits) || (hasDigits && hasSymbols)) {
      this.passwordStrength = 'medium';
    } else if (hasLetters && hasDigits && hasSymbols) {
      this.passwordStrength = 'strong';
    } else {
      this.passwordStrength = 'weak';
    }
  }
}
<div class="wrapper">
  <div class="password-strength" [ngClass]="passwordStrength">
    <input type="password" formControlName="password" placeholder="Enter your password">
    <div class="strength-block">
      <div class="strength-section weak"></div>
      <div class="strength-section medium"></div>
      <div class="strength-section strong"></div>
    </div>
  </div>
</div>
